import { useSelector, useDispatch } from 'react-redux';

import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { Fragment ,useEffect, useState } from 'react'; 
import { uiActions } from './store/ui-slice'; 
import Notification from './components/UI/'

function App() {  

  const dispatch = useDispatch();

 const showCart =  useSelector(state => state.ui.cartIsVisable); 
 const cart = useSelector((state) => state.cart);  
 const notification = useState(state => state.ui.notification);

 useEffect(()=> { 
  const sendCartData = async () => {  
    dispatch(uiActions.showNotification({
      status: 'pending',
      title: 'Sending...',
      message: 'Sending cart data!'
    })
  );
   const response = await fetch('https://practice-project-aa89e-default-rtdb.firebaseio.com/cart.json', { 
    method: 'PUT', 
    body: JSON.stringify(cart),
  }); 

  if(!response.ok) { 
    throw new Error('Sending Cart data failed')
  }



  dispatch(uiActions.showNotification({
    status: 'success',
    title: 'Success...',
    message: 'Sent cart data successfully!'
  })
);
  };

  sendCartData().catch((error) =>{ 
    dispatch(uiActions.showNotification({
      status: 'Error',
      title: 'Error',
      message: 'Sent cart data failed!',
    })
  );
  });

  }, [cart, dispatch]);

  return ( 

    <Fragment> 
      <Notification />
    <Layout>
     {showCart && <Cart />}
      <Products />
    </Layout> 
    </Fragment>
  );
}

export default App;
